package com.epam.customer.controller;

import com.epam.customer.domain.Product;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RequestMethod;
import org.springframework.web.bind.annotation.RestController;

import java.math.BigDecimal;
import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;

@RestController
@RequestMapping(path = "api")
public class ProductsController {

    @RequestMapping(path = "products", method = RequestMethod.GET)
    public List<Product> getAllProducts() {
        List<Product> products = new ArrayList<>();

        List<String> names = Arrays.asList("Nike", "Adidas", "Converse", "Reebok", "Puma", "New Balance");

        for (int i = 0; i < 6; i++) {
            products.add(createProduct((long) i + 1, names.get(i % names.size()), BigDecimal.valueOf(1.1 + i)));
        }

        return products;
    }

    private Product createProduct(Long id, String name, BigDecimal amount) {
        Product product = new Product();
        product.setId(id);
        product.setName(name);
        product.setAmount(amount);
        product.setDescription("Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nullam venenatis venenatis purus nec aliquam. Proin non tincidunt tellus. Vestibulum semper sit amet arcu vel mollis. Cras eu placerat massa. Integer fringilla non est ut luctus. Sed facilisis ut lorem ut feugiat. Integer nec bibendum enim, ut dapibus nibh. Nunc sollicitudin nisi dolor, egestas congue urna dignissim vitae. Nulla sem est, pellentesque ac suscipit vitae, tempor a diam. Aliquam lacus nisi, sollicitudin tristique lacus id, mattis dapibus nisl.");
        product.setBestSeller("data:image/jpeg;base64,");
        return product;
    }

}